$(document).ready(function(){$(".btn-init").on("click",function(){$("#main-container").slideToggle(function(){$("html, body").animate({scrollTop:$("#main-container").offset().top},500)})}),$(function(){$("#main-submission-form").on("submit",function(a){$.ajax({type:"post",url:"submit.php",data:$(this).serialize(),success:function(){$("#main-submission-form").fadeOut(),$(".success").show().animate({opacity:"1"},2e3)}}),a.preventDefault()})})});